go :- initBoardRouge, placementReineRouge([a1(double),a2(triple),a3(simple),a4(double),a5(double),a6(triple),b1(double),b2(simple),b3(triple),b4(simple),b5(triple),b6(simple),c1(simple),c2(triple),c3(double),c4(triple),c5(simple),c6(double),d1(triple),d2(simple),d3(double),d4(simple),d5(triple),d6(double),e1(double),e2(triple),e3(simple),e4(triple),e5(simple),e6(triple),f1(double),f2(simple),f3(triple),f4(double),f5(double),f6(simple)],A),
	placementSbireRouge(A,5,F),
	placementReineOcre(F,C),
	placementSbireOcre(C,5,BoardStart),
	write('Les kalistas et sbires ont tous ete installes. Que la partie commence: '), nl,
	disp(BoardStart),
	tourDeJeuRouge(BoardStart,BoardEnd),
	tourDeJeuOcre(BoardEnd,Boardsuivant).


initBoardRouge :-
  write('Plateau de jeu'),
  nl,  /*On va utiliser disp, qui est définie juste au-dessus, à la ligne 46 */ 
  disp(['a1(double)','a2(triple)','a3(simple)','a4(double)','a5(double)','a6(triple)','b1(double)','b2(simple)','b3(triple)','b4(simple)','b5(triple)','b6(simple)','c1(simple)','c2(triple)','c3(double)','c4(triple)','c5(simple)','c6(double)','d1(triple)','d2(simple)','d3(double)','d4(simple)','d5(triple)','d6(double)','e1(double)','e2(triple)','e3(simple)','e4(triple)','e5(simple)','e6(triple)','f1(double)','f2(simple)','f3(triple)','f4(double)','f5(double)','f6(simple)']),
  nl.

  
placementReineRouge(Board,Newboard):-  write('Joueur rouge: veuillez entrer votre Kalista sur une des deux dernieres lignes du plateau. '), nl, read(N),rmouvement(Board,N,Newboard),disp(Newboard). 

placementSbireRouge(NewBoard3,0,NewBoard3) :- !.  
placementSbireRouge(A,K,Newboard3):- write('Joueur rouge: veuillez entrer un sbire rouge'), nl, read(Z),rsmouvement(A,Z,Newnewboard),disp(Newnewboard),K1 is K-1, placementSbireRouge(Newnewboard,K1,Newboard3). 

placementReineOcre(BoardOcre,NewboardOcre) :- write('Joueur ocre: veuillez entrer votre Kalista sur une des deux premieres lignes du plateau. '), nl, read(P), omouvement(BoardOcre,P,NewboardOcre), disp(NewboardOcre). 

placementSbireOcre(NewBoard4,0,NewBoard4) :- !. 
placementSbireOcre(C,K,NewBoard4) :- write('Joueur ocre: veuillez entrer un sbire ocre'), nl, read(Y), osmouvement(C,Y,Newnewnewboard), nl, disp(Newnewnewboard), K2 is K-1, placementSbireOcre(Newnewnewboard,K2,NewBoard4). 

tourDeJeuRouge(Board,Boardmodif) :- write('Joueur rouge : veuillez indiquer la case ou se trouve la piece que vous souhaitez deplacer.'), read(Player), write('endroit ou vous pouvez vous deplacer'), nl, possibleMoves(Player,PossibleMoveList), write('Liste des endroits ou vous pouvez aller depuis cette piece: '),nl,afficherListe(PossibleMoveList). /*deplacerJoueur(Board,Player,DP,Boardmodif). */  

















